import React, { useState } from 'react';
import ScrollToBottom from 'react-scroll-to-bottom';
import { url_rooms } from "./../Routes"
import { useHistory } from 'react-router-dom';

const history = useHistory();


const handleEnterChat = (event) => {
    event.preventDefault();
    const data = event.target.value;
    var sepData = data.split(".")
    const chatId = sepData[0]
    history.push(`/chat?room=${chatId}`);
}

const Rooms = ({rooms}) => (
    <select id="room-list" size="10" onChange={handleEnterChat}>
        {rooms.map((item) => <option key={item.id}>{item.id}. {item.name}</option>)}
    </select>
)

export default Rooms;
