import React, { useState, useEffect } from "react";
import queryString from 'query-string';
import io from 'socket.io-client';

import './Chat.css'
import '../general.css'

import InfoBar from '../InfoBar/InfoBar';
import Input from '../Input/Input';
import Messages from '../Messages/Messages';
let socket;



const Chat = ({ location }) => {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const ENDPOINT = 'ec2-18-219-25-136.us-east-2.compute.amazonaws.com:5000'


    useEffect(() => {
        const {room, name} = queryString.parse(location.search);

        socket = io(ENDPOINT);

        setName(name);
        setRoom(room);

        socket.emit('join', {name, room}, () =>{

        });
        socket.on('messages', (messages) => {
            console.log(messages);
            setMessages(messages);
        })

        socket.on('message', (message) => {
            console.log(message);
            setMessages(messages => [...messages, message]);
        });

        return () => {
            socket.emit('disconnect', {room, name});
            socket.off();
        }


    }, [ENDPOINT, location.search]);


    const sendMessage = (event) => {
        event.preventDefault();

        if (message[0] === '/'){
            let command = message.substring(1).trim().toLowerCase();
            socket.emit('command', {command, name, room}, () => setMessage(''));

        } else if (message) {
            socket.emit('sendMessage', {message, name, room}, () => setMessage(''));
        }

    }



    return (
        <div id='main'> 
            <article>      
                <div className='Full'>
                    <div className='Console'>
                        <div className={'ChatContainer'}>
                            <InfoBar room={room}/>
                            <Messages messages={messages} name={name}/>
                            <Input message={message} setMessage={setMessage} sendMessage={sendMessage}/>
                        </div>
                    </div>
                </div>
            </article>
            <nav>
                {/* <form class="mini-form" onSubmit={this.handleSubmit}>
                    <div>
                        <input type="text" value={this.state.newroom} onChange={this.handleChange} placeholder="room name"/>
                    </div>
                    <br></br>
                    <div><input type="submit" value="Create Room"></input></div>

                </form>
                <br></br>
                <br></br>
                <hr></hr>
                <h3>Rooms</h3>

                <div>
                    <select id="room-list" size="10" onChange={this.handleEnterChat}>
                        {room}
                    </select>

                </div>      */}
            </nav>
        </div>         
    )
}

export default Chat;
